// Using import to access different files
import React, {useEffect, useState} from "react";

import TransactionLists from "./TransactionsList";
import SearchList from "./SearchList";



// Function to be used in AccountContainer

function AccountContainer() {

    const [transaction, setTransaction] = useState ([])

    const [query, setQuery] = useState("")
    // Using useEffect in fetching data from remote
    useEffect(() => {
        fetch(" http://localhost:3000/transactions" + query)
        .then((resp) => resp.json())
        .then(transaction => setTransaction(transaction))
    }, [query])

    // function to be used to handle the search action

    function handleSearch(e) {
        setQuery (e.target.value)
    }

    // Using return to display on the DOM

    return (
        <div>
            <SearchList handleSearch={handleSearch} />
            <TransactionLists transactions={transaction} />
            <NewTransactionForm />
        </div>
    );
}

// using export to enable the file to be used elsewhere

export default AccountContainer;
